import{createSlice}from"@reduxjs/toolkit";const initialState={token:null,user:null},userSlice=createSlice({name:"user",initialState:initialState,reducers:{loginSuccess:(e,t)=>{e.token=t.payload.token,e.user=t.payload.user},logout:e=>{e.token=null,e.user=null},updateUser:(e,t)=>{e.user&&(e.user={...e.user,...t.payload})}}});export const{loginSuccess:loginSuccess,logout:logout,updateUser:updateUser}=userSlice.actions;export default userSlice.reducer;